import React from 'react'
import styled from 'styled-components'

const Container=styled.div`
display: flex;
flex-direction: column;
align-items: center;
padding: 0px 0px 64px;
gap: 96px;
position: absolute;
width: 1512px;
height: 1167.5px;
left: 0px;
top: 120px;
`
const Top=styled.div`
width: 1512px;
height: 795.5px;
background: #061237;
flex: none;
order: 0;
flex-grow: 0;
`

const Bottom=styled.div`
width: 1120px;
height: 212px;
flex: none;
order: 1;
flex-grow: 0;
`
const Main=styled.div`
display: flex;
flex-direction: row;
align-items: flex-start;
padding: 0px;
gap: 40px;
position: absolute;
width: 980px;
height: 587.5px;
left: 266px;
top: 144px;
`
const Left=styled.div`
width: 470px;
height: 587.5px;
flex: none;
order: 0;
flex-grow: 0;`

const Head=styled.div``
const Disc=styled.div`
position: absolute;
width: 420.98px;
height: 108px;
left: 0px;
top: 224px;

font-family: 'Inter';
font-style: normal;
font-weight: 400;
font-size: 18px;
line-height: 36px;
display: flex;
align-items: center;
letter-spacing: -0.36px;
color: #FFFFFF;
`
const Explore=styled.div``

const Right=styled.div`
width: 470px;
height: 587.5px;
flex: none;
order: 0;
flex-grow: 0;`

const BottomDiv=styled.div``
const WhiteDiv=styled.div``
const BlueDiv=styled.div``
const TopDiv=styled.div``
const Vector=styled.div``

const Text=styled.div`
position: absolute;
width: 665.08px;
height: 40px;
left: calc(50% - 665.08px/2 + 0.1px);
top: calc(50% - 40px/2 - 86px);
font-family: 'Inter';
font-style: normal;
font-weight: 700;
font-size: 32px;
line-height: 40px;
display: flex;
align-items: center;
text-align: center;
letter-spacing: -0.96px;
color: #061237;
`
const Company=styled.div`
display: flex;
flex-direction: row;
align-items: flex-start;
padding: 0px;
gap: 32px;
position: absolute;
width: 1120px;
height: 138px;
left: calc(50% - 1120px/2);
top: 74px;
`
const BottomImg=styled.div`
width: 256px;
height: 138px;
flex: none;
order: 0;
flex-grow: 0;`
const Intro = () => {
  return (
    <Container>
      <Top>
        <TopDiv></TopDiv>
        <Main>
            <Left>
                <Head></Head>
                <Disc>Opus includes everything you need to build a beautiful website for your business. Built to perform and look good doing so.</Disc>
                <Explore></Explore>
            </Left>
            <Right></Right>
        </Main>
        <BlueDiv></BlueDiv>
        <WhiteDiv></WhiteDiv>
        <Vector></Vector>
        <BottomDiv></BottomDiv>
      </Top>
      <Bottom>
        <Text>Trusted and loved by the world’s best teams</Text>
        <Company>
            <BottomImg><img src="" /></BottomImg>
            <BottomImg><img src="" /></BottomImg>
            <BottomImg><img src="" /></BottomImg>
            <BottomImg><img src="" /></BottomImg>
        </Company>
      </Bottom>
    </Container>
  )
}

export default Intro
